gh alias set --clobber  myrev 'search prs --state=open --review-requested=@me --json repository,author,number,title,updatedAt,url --template "Showing {{len .}} pull requests{{\"\\n\"}}{{tablerow \"REPO\" \"AUTHOR\" \"ID\" \"TITLE\" \"UPDATED\" \"URL\"}}{{\"\\n\"}}{{range .}}{{tablerow .repository.nameWithOwner .author.login \(printf \"\#%v\" .number \| autocolor \"green\"\) .title \(timeago .updatedAt\) .url}}{{end}}"'
gh alias set --clobber  myprs 'search prs --state=open --author=@me --json repository,number,title,updatedAt,url --template "Showing {{len .}} pull requests{{\"\\n\"}}{{tablerow \"REPO\" \"ID\" \"TITLE\" \"UPDATED\" \"URL\"}}{{\"\\n\"}}{{range .}}{{tablerow .repository.nameWithOwner \(printf \"\#%v\" .number \| autocolor \"green\"\) .title \(timeago .updatedAt\) .url}}{{end}}"'
